import com.eviware.soapui.support.GroovyUtils

def Yest_Day = context.expand('${Properties#Yesterday_date}')
def Today_Day = context.expand('${Properties#Today_date}')

log.info(Yest_Day)
log.info(Today_Day)
def StepName = context.getCurrentStep().name
def CaseName = context.getTestCase().name
def SuiteName = context.getTestCase().getTestSuite().name

def reqXML = context.expand('${GetCurrencyReq#Request}')

def groovyUtils = new com.eviware.soapui.support.GroovyUtils( context )
def requsetHolder = groovyUtils.getXmlHolder( reqXML )
requsetHolder.namespaces['ns1'] = "http://tempuri.org/"
requsetHolder.setNodeValue("//ns1:CurrencyFrom","GBP")
requsetHolder.setNodeValue("//ns1:CurrencyTo","INR")
requsetHolder.setNodeValue("//ns1:RateDate",Yest_Day)
requsetHolder.setNodeValue("//ns1:Amount","100")

def ModifiedXML = requsetHolder.getXml()
testRunner.testCase.getTestStepByName("GetCurrencyReq").setPropertyValue("Request",ModifiedXML)

testRunner.testCase.testSuite.project.testSuites[SuiteName].testCases[CaseName].testSteps["GetCurrencyReq"].run(testRunner, context);
def res_Xml = context.expand('${GetCurrencyReq#Response}')
def responseHolder = groovyUtils.getXmlHolder( res_Xml )
responseHolder.namespaces['ns2'] = "http://tempuri.org/"
def res = responseHolder.getNodeValue("//ns2:GetConversionAmountResult")
testRunner.testCase.getTestStepByName("Properties").setPropertyValue("Yes_Rate",res)
