import com.eviware.soapui.support.GroovyUtils
//Define variables
def email= context.expand('${Properties#email}')
def firstname= context.expand('${Properties#firstname}')
def lastname= context.expand('${Properties#lastname}')
def regionCode= context.expand('${Properties#regionCode}')
def region= context.expand('${Properties#region}')
def countryId= context.expand('${Properties#countryId}')
def item= context.expand('${Properties#item}')
def telephone= context.expand('${Properties#telephone}')
def postcode= context.expand('${Properties#postcode}')
def city= context.expand('${Properties#city}')

def defaultShipping= context.expand('${Properties#defaultShipping}')
def defaultBilling= context.expand('${Properties#defaultBilling}')

def StepName = context.getCurrentStep().name 
log.info(StepName) 
def TcName = context.getTestCase().name 
log.info(TcName) 
def TestSuiteName = context.getTestCase().getTestSuite().name 
log.info(TestSuiteName)

def reqXML = context.expand('${Create_Customer_SOAP_Request#Request}')
log.info(reqXML)
//Set email,firstname,lastname in Request XML
def groovyUtils = new com.eviware.soapui.support.GroovyUtils( context )
def requsetHolder = groovyUtils.getXmlHolder( reqXML )
requsetHolder.namespaces = "http://soap.upskills.in/soap/default?services=customerCustomerRepositoryV1"
requsetHolder.setNodeValue("//email",email)
requsetHolder.setNodeValue("//firstname",firstname)
requsetHolder.setNodeValue("//lastname",lastname)
requsetHolder.setNodeValue("//regionCode",regionCode)
requsetHolder.setNodeValue("//addresses/region/region",region)
requsetHolder.setNodeValue("//countryId",countryId)
requsetHolder.setNodeValue("//street/item",item)
requsetHolder.setNodeValue("//telephone",telephone)
requsetHolder.setNodeValue("//postcode",postcode)
requsetHolder.setNodeValue("//city",city)
requsetHolder.setNodeValue("//item/firstname",firstname)
requsetHolder.setNodeValue("//item/lastname",lastname)
requsetHolder.setNodeValue("//defaultShipping",defaultShipping)
requsetHolder.setNodeValue("//defaultBilling",defaultBilling)

def ModifiedXML = requsetHolder.getXml()
testRunner.testCase.getTestStepByName("Create_Customer_SOAP_Request").setPropertyValue("Request",ModifiedXML) 
//this comman will add a new customer
testRunner.testCase.testSuite.project.testSuites[TestSuiteName].testCases[TcName].testSteps["Create_Customer_SOAP_Request"].run(testRunner,context)

//def id= def city= context.expand('${Properties#customerId}')

//STep2-Fetch Customer details by customer Id
//Set id value in customer Details request 
// Add Property
testRunner.testCase.testSuite.project.addProperty("DOB")
def reqXML1n        ggg                                  = context.expand('${Verify_Customer_Details_SOAP Request#Request}')
def requsetHolder1 = groovyUtils.getXmlHolder( reqXML1 )
requsetHolder1.namespaces = "http://soap.upskills.in/soap/default?services=customerCustomerRepositoryV1"
requsetHolder.setNodeValue("//customerId",customerId)
ef ModifiedXML1 = requsetHolder1.getXml()
testRunner.testCase.getTestStepByName("Verify_Customer_Details_SOAP Request").setPropertyValue("Request",ModifiedXML) 
testRunner.testCase.testSuite.project.testSuites[TestSuiteName].testCases[TcName].testSteps["Verify_Customer_Details_SOAP Request"].run(testRunner,context)

